import React, { useEffect, useRef, useMemo } from "react";
import { Line } from "react-chartjs-2";
import { Chart, CategoryScale, LinearScale, PointElement, LineElement, Tooltip } from "chart.js";

Chart.register(CategoryScale, LinearScale, PointElement, LineElement, Tooltip);

function StockChart({ data, ticker }) {
  const chartRef = useRef(null);
  const initializedTimes = useRef(new Set());

  // Get only 15-minute interval times
  const get15MinuteTimes = useMemo(() => {
    const times = new Set();
    data.forEach(point => {
      const minutes = new Date(`2000-01-01T${point.time}`).getMinutes();
      if (minutes % 15 === 0) {
        times.add(point.time);
      }
    });
    return Array.from(times).sort();
  }, [data]);

  useEffect(() => {
    const chartInstance = chartRef.current;
    return () => {
      if (chartInstance) chartInstance.destroy();
    };
  }, [ticker]); // Destroy on ticker change

  const chartData = {
    labels: data.map(p => p.time),
    datasets: [{
      label: "Stock Price",
      data: data.map(p => p.price),
      borderColor: "rgba(75,192,192,1)",
      fill: false
    }]
  };

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    scales: {
      x: {
        type: 'category',
        ticks: {
          callback: (value, index) => {
            const time = data[index]?.time;
            // Only show if it's one of our 15-minute marks
            return get15MinuteTimes.includes(time) ? time : null;
          },
          autoSkip: false,
          maxRotation: 0,
          minRotation: 0
        }
      },
      y: {
        /* y-axis config remains same */
      }
    }
  };

  return (
    <div style={{ width: "100%", height: "400px" }}>
      <Line 
        ref={chartRef}
        data={chartData}
        options={options}
        key={ticker} // Force fresh instance on ticker change
      />
    </div>
  );
}

export default StockChart;
