import React, { useEffect, useRef, useMemo } from "react";
import { Line } from "react-chartjs-2";
import { Chart, CategoryScale, LinearScale, PointElement, LineElement, Tooltip } from "chart.js";

// Register the required scales, elements, and plugins
Chart.register(CategoryScale, LinearScale, PointElement, LineElement, Tooltip);

function StockChart({ data }) {
  const chartRef = useRef(null);

  useEffect(() => {
    const chartInstance = chartRef.current;

    // Force the chart to update when data changes
    if (chartInstance && chartInstance.canvas) { // Check if chart instance and canvas exist
      chartInstance.update();
    }

    return () => {
      // Destroy the chart instance when the component unmounts
      if (chartInstance) {
        chartInstance.destroy();
      }
    };
  }, [data]); // Add `data` as a dependency

  const chartData = useMemo(() => {
    return {
      labels: data.map((point) => point.time),
      datasets: [
        {
          label: "Stock Price",
          data: data.map((point) => point.price),
          borderColor: "rgba(75,192,192,1)",
          fill: false,
        },
      ],
    };
  }, [data]); // Recompute `chartData` when `data` changes

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      tooltip: {
        enabled: true,
        mode: "index",
        intersect: false,
        backgroundColor: "rgba(0, 0, 0, 0.8)",
        titleColor: "#fff",
        bodyColor: "#fff",
        borderColor: "#fff",
        borderWidth: 1,
        displayColors: false,
        callbacks: {
          title: () => "",
          label: (context) => {
            const price = context.raw.toFixed(2);
            return `$${price}`;
          },
        },
      },
    },
    scales: {
      x: {
        type: "category",
        title: {
          display: true,
          text: "Time",
          color: "#666",
          font: {
            size: 14,
            weight: "bold",
          },
          padding: { top: 10, bottom: 10 },
        },
        grid: {
          display: false,
        },
        ticks: {
          color: "#666",
          font: {
            size: 12,
          },
          // Filter the labels to show only every 15 minutes
          callback: (value, index, values) => {
            if (index % 15 === 0) { // Show label every 15 data points
              return data[index]?.time || null;
            }
            return null;
          },
        },
      },
      y: {
        type: "linear",
        title: {
          display: true,
          text: "Price (USD)",
          color: "#666",
          font: {
            size: 14,
            weight: "bold",
          },
          padding: { top: 0, bottom: 10 },
        },
        grid: {
          color: "#eee",
        },
        ticks: {
          color: "#666",
          font: {
            size: 12,
          },
        },
      },
    },
  };

  return (
    <div style={{ width: "100%", height: "400px" }}>
      <Line ref={chartRef} data={chartData} options={options} />
    </div>
  );
}

export default StockChart;
