import React, { useEffect, useRef } from "react";
import { Line } from "react-chartjs-2";
import { Chart, CategoryScale, LinearScale, PointElement, LineElement, Tooltip } from "chart.js";

// Register Chart.js components
Chart.register(CategoryScale, LinearScale, PointElement, LineElement, Tooltip);

function StockChart({ data, ticker }) {
  const chartRef = useRef(null);

  // Helper function to format time with AM/PM
  const formatTimeWithAMPM = (timeString) => {
    if (!timeString) return '';

    // If time already contains AM/PM, return as-is
    if (timeString.includes('AM') || timeString.includes('PM')) {
      return timeString;
    }

    // Parse 24-hour format
    const [hours, minutes] = timeString.split(':');
    const hourNum = parseInt(hours, 10);

    // Correct AM/PM determination (12 PM is noon, 12 AM is midnight)
    const ampm = hourNum >= 12 && hourNum < 24 ? 'PM' : 'AM';
    const displayHour = hourNum % 12 || 12; // Convert 0 or 24 to 12

    return `${displayHour}:${minutes} ${ampm}`;
  };

  // Chart data configuration
  const chartData = {
    labels: data.map(p => p.time),
    datasets: [{
      label: "Stock Price",
      data: data.map(p => p.price),
      borderColor: "rgba(75,192,192,1)", // Line color
      borderWidth: 2,
      pointRadius: 3, // Size of data points
      pointHoverRadius: 5, // Size when hovered
      fill: false // No area fill under line
    }]
  };

  // Chart options configuration
  const options = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      tooltip: {
        enabled: true,
        mode: "index",
        intersect: false,
        backgroundColor: "rgba(0, 0, 0, 0.8)", // Tooltip background
        titleColor: "#fff",
        bodyColor: "#fff",
        borderColor: "#fff",
        borderWidth: 1,
        displayColors: false, // Don't show color boxes in tooltip
        callbacks: {
          title: (context) => {
            // Format tooltip time with AM/PM
            return formatTimeWithAMPM(context[0].label);
          },
          label: (context) => {
            // Format price to 2 decimal places
            const price = context.parsed.y.toFixed(2);
            return `$${price}`;
          }
        }
      },
      legend: {
        display: false // Hide legend
      }
    },
    scales: {
      x: {
        type: 'category',
        title: {
          display: true,
          text: 'Time',  // X axis label 
          color: '#666',
          font: {
            size: 14,
            weight: 'bold'
          },
          padding: { top: 10, bottom: 10 }
        },
        ticks: {
          callback: (value, index) => {
            // Only want the x-axis to display in 15 minute intervals
            const time = data[index]?.time;
            if (!time) return undefined;

            // Format with AM/PM
            const formattedTime = formatTimeWithAMPM(time);

            // Extract minutes to check interval
            const minutes = time.includes(':') ? 
              time.split(':')[1].split(' ')[0] : // Handle both "9:30" and "9:30 AM" formats
              '00';

            // Only show every 15 minutes
            return parseInt(minutes) % 15 === 0 ? formattedTime : undefined;
          },
          autoSkip: false, // Show all ticks (we filter them manually)
          maxRotation: 0, // No rotation
          minRotation: 0,
          color: '#666',
          font: {
            size: 10, // Changed from 12 to 10 for smaller text
            family: "'Helvetica Neue', 'Helvetica', 'Arial', sans-serif" // Optional: specify font family
          }
        },
        grid: {
          display: false // Hide vertical grid lines
        }
      },
      y: {
        title: {
          display: true,
          text: 'Price (USD)', // Y axis label
          color: '#666',
          font: {
            size: 14,
            weight: 'bold'
          },
          padding: { top: 0, bottom: 10 }
        },
        ticks: {
          color: '#666',
          font: {
            size: 12
          }
        },
        grid: {
          color: '#eee' // Light gray horizontal grid lines
        }
      }
    }
  };

  // Clean up chart instance on unmount
  useEffect(() => {
    const chartInstance = chartRef.current;
    return () => {
      if (chartInstance) chartInstance.destroy();
    };
  }, [ticker]);

  return (
    <div style={{ 
      width: "100%", 
      height: "400px", 
      border: "1px solid #ddd", 
      borderRadius: "8px" 
    }}>
      <Line 
        ref={chartRef}
        data={chartData}
        options={options}
        key={ticker}
      />
    </div>
  );
}

export default StockChart;
