import React, { useEffect, useRef, useState } from "react";
import { Line } from "react-chartjs-2";
import { Chart, CategoryScale, LinearScale, PointElement, LineElement, Tooltip } from "chart.js";

Chart.register(CategoryScale, LinearScale, PointElement, LineElement, Tooltip);

function StockChart({ initialData }) {
  const chartRef = useRef(null);
  const [data, setData] = useState(initialData);

  // Format time to "H:MM" (e.g., "9:30") without seconds
  const formatTime = (timeString) => {
    const [hours, minutes] = timeString.split(":");
    return `${parseInt(hours, 10)}:${minutes}`; // Removes leading zero and seconds
  };

  // Simulate real-time updates every minute
  useEffect(() => {
    const interval = setInterval(() => {
      const newTime = new Date();
      const hours = newTime.getHours().toString().padStart(2, "0");
      const minutes = newTime.getMinutes().toString().padStart(2, "0");
      const timeLabel = `${hours}:${minutes}`; // Format as "HH:MM"

      const newDataPoint = {
        time: timeLabel,
        price: Math.random() * 100, // Replace with real data
      };

      setData((prevData) => [...prevData, newDataPoint]);
    }, 60000); // Update every minute

    return () => clearInterval(interval);
  }, []);

  // Prepare chart data with formatted times
  const chartData = {
    labels: data.map((point) => formatTime(point.time)), // Format all times
    datasets: [
      {
        label: "Stock Price",
        data: data.map((point) => point.price),
        borderColor: "rgba(75,192,192,1)",
        fill: false,
      },
    ],
  };

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      tooltip: {
        callbacks: {
          title: (tooltipItems) => formatTime(tooltipItems[0].label), // Format tooltip time
          label: (context) => `$${context.raw.toFixed(2)}`,
        },
      },
    },
    scales: {
      x: {
        type: "category",
        ticks: {
          callback: (value, index) => {
            const [hours, minutes] = data[index].time.split(":");
            return parseInt(minutes) % 15 === 0 ? formatTime(`${hours}:${minutes}`) : null;
          },
          maxRotation: 0,
          autoSkip: false,
        },
        grid: { display: false },
      },
      y: {
        title: { text: "Price (USD)" },
        grid: { color: "#eee" },
      },
    },
  };

  return (
    <div style={{ width: "100%", height: "400px" }}>
      <Line ref={chartRef} data={chartData} options={options} />
    </div>
  );
}

export default StockChart;
