import React, { useState, useEffect } from "react";
import "./App.css";
import axios from "axios";
import SearchBar from "./SearchBar";
import StockChart from "./StockChart";
import Parameters from "./Parameters";

function App() {
  // State management for stock data and parameters
  const [selectedTicker, setSelectedTicker] = useState(null);
  const [stockData, setStockData] = useState([]);
  const [drift, setDrift] = useState(0.0002);
  const [volatility, setVolatility] = useState(0.05);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  const hostname = "localhost";
  const port = process.env.port || 5001;

  // Fetch stock data when the selected ticker changes
  useEffect(() => {
    let intervalId;

    const fetchStockData = async () => {
      try {
        const response = await axios.get(
          `http://${hostname}:${port}/api/stock/${selectedTicker}`
        );
        console.log("Fetched new data:", response.data.prices); // Log the fetched data
        setStockData(response.data.prices);
        setError(null);
      } catch (err) {
        setError("Failed to fetch stock data. Please check the backend server.");
        console.error(err);
      }
    };

    if (selectedTicker) {
      fetchStockData(); // Fetch immediately on selection
      intervalId = setInterval(fetchStockData, 60000); // Then every minute
    }

    return () => {
      if (intervalId) {
        clearInterval(intervalId); // Cleanup on unmount
      }
    };
  }, [selectedTicker]);

  // Handle parameter updates
  const handleParameterUpdate = async (newDrift, newVolatility) => {
    try {
      // Send updates to backend
      await axios.post(
        `http://${hostname}:${port}/api/stock/${selectedTicker}/parameters`,
        {
          drift: newDrift,
          volatility: newVolatility,
        }
      );

      // Update local state
      setDrift(newDrift);
      setVolatility(newVolatility);

      // Show success message
      setSuccess(true);
      setTimeout(() => setSuccess(false), 3000);
    } catch (err) {
      setError("Failed to update parameters. Please check the backend server.");
      console.error(err);
    }
  };

  return (
    <div className="App">
      {/* Header Section with gradient background */}
      <div className="header-section">
        <h1>Real-Time Stock Prices</h1>
        <h5>A web app by Hamzah Muhammad</h5>
        <h5>(t+1) stock price calculated using the GBM Formula</h5>
        <h2>Select a Stock Ticker to get started</h2>
        <SearchBar onSelectTicker={setSelectedTicker} />
        {error && <p className="error-message">{error}</p>}
      </div>

      {/* Main Content Area */}
      <div className={`content-container ${selectedTicker ? "filled" : ""}`}>
        {selectedTicker ? (
          <>
            {/* Stock Chart Visualization */}
            <div className="stock-chart-container">
              <div className="stock-chart-wrapper">
                <StockChart data={stockData} />
              </div>
            </div>

            {/* Parameters Section */}
            <div className="parameters-header-section">
              <h3>Model Parameters</h3>
            </div>
            <div className="parameters-container">
              <div className="parameters-card">
                <Parameters
                  drift={drift}
                  volatility={volatility}
                  onUpdate={handleParameterUpdate}
                />
              </div>
            </div>
          </>
        ) : (
          /* Empty State */
          <div className="empty-state">
            <h3>Please select a stock ticker to view data</h3>
          </div>
        )}
      </div>

      {/* Success Notification */}
      {success && (
        <div className="success-notification">
          <div className="success-message">
            <span>âœ“</span> Parameters updated successfully!
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
