import React, { useState, useEffect } from "react";

function Parameters({ drift, volatility, onUpdate }) {
  const [newDrift, setNewDrift] = useState(drift);
  const [newVolatility, setNewVolatility] = useState(volatility);
  const [showSuccess, setShowSuccess] = useState(false);

  useEffect(() => {
    let timer;
    if (showSuccess) {
      timer = setTimeout(() => setShowSuccess(false), 3000);
    }
    return () => clearTimeout(timer);
  }, [showSuccess]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isNaN(newDrift) || isNaN(newVolatility) || newVolatility < 0) {
      alert("Please enter valid values. Volatility must be non-negative.");
      return;
    }
    onUpdate(newDrift, newVolatility);
    setShowSuccess(true);
  };

  return (
    <div className="parameters-master-container">
      <div className="parameters-center-container">
        <div className="parameters-form-section">
          <form onSubmit={handleSubmit} className="parameters-form">
            <div className="parameter-control-group">
              <label className="parameter-control-label">
                Drift:
                <input
                  type="number"
                  step="0.0001"
                  value={newDrift}
                  onChange={(e) => setNewDrift(parseFloat(e.target.value))}
                  className="parameter-control-input"
                />
              </label>
            </div>
            <div className="parameter-control-group">
              <label className="parameter-control-label">
                Volatility:
                <input
                  type="number"
                  step="0.01"
                  value={newVolatility}
                  onChange={(e) => setNewVolatility(parseFloat(e.target.value))}
                  className="parameter-control-input"
                />
              </label>
            </div>
            <button type="submit" className="parameter-action-button">
              Update Parameters
            </button>
          </form>
        </div>

        {showSuccess && (
          <div className="parameter-feedback-sidepanel">
            <div className="feedback-message-container">
              <span className="feedback-icon">âœ“</span>
              Parameters updated successfully!
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default Parameters;
