import React, { useEffect, useRef } from "react";
import { Line } from "react-chartjs-2";
import { Chart, CategoryScale, LinearScale, PointElement, LineElement, Tooltip } from "chart.js";

Chart.register(CategoryScale, LinearScale, PointElement, LineElement, Tooltip);

function StockChart({ data, ticker }) {
  const chartRef = useRef(null);

  const chartData = {
    labels: data.map(p => p.time),
    datasets: [{
      label: "Stock Price",
      data: data.map(p => p.price),
      borderColor: "rgba(75,192,192,1)",
      fill: false
    }]
  };

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    scales: {
      x: {
        type: 'category',
        ticks: {
          callback: (value, index) => {
            const time = data[index]?.time;
            if (!time) return undefined;

            // Format time to show only hours and minutes
            const [hours, minutes] = time.split(':');
            const formattedTime = `${hours}:${minutes}`;

            // Only show labels at 15-minute intervals
            return parseInt(minutes) % 15 === 0 ? formattedTime : undefined;
          },
          autoSkip: false,
          maxRotation: 0,
          minRotation: 0
        },
        grid: {
          display: false
        }
      },
      y: {
        /* y-axis config remains same */
      }
    }
  };

  useEffect(() => {
    const chartInstance = chartRef.current;
    return () => {
      if (chartInstance) chartInstance.destroy();
    };
  }, [ticker]);

  return (
    <div style={{ width: "100%", height: "400px" }}>
      <Line 
        ref={chartRef}
        data={chartData}
        options={options}
        key={ticker}
      />
    </div>
  );
}

export default StockChart;
