import React, { useEffect, useRef } from "react";
import { Line } from "react-chartjs-2";
import { Chart, CategoryScale, LinearScale, PointElement, LineElement } from "chart.js";

// Register the required scales and elements
Chart.register(CategoryScale, LinearScale, PointElement, LineElement);

function StockChart({ data }) {
  const chartRef = useRef(null);

  useEffect(() => {
    const chartInstance = chartRef.current;

    return () => {
      // Destroy the chart instance when the component unmounts
      if (chartInstance) {
        chartInstance.destroy();
      }
    };
  }, []);

  const chartData = {
    labels: data.map((point) => point.time),
    datasets: [
      {
        label: "Stock Price",
        data: data.map((point) => point.price),
        borderColor: "rgba(75,192,192,1)",
        fill: false,
      },
    ],
  };

  const options = {
    responsive: true, // Make the chart responsive
    maintainAspectRatio: false, // Allow the chart to resize freely
    scales: {
      x: {
        type: "category", // Ensure the x-axis uses the 'category' scale
        title: {
          display: true, // Enable the X-axis title
          text: "Time", // X-axis label text
          color: "#666", // Label color
          font: {
            size: 14, // Label font size
            weight: "bold", // Label font weight
          },
          padding: { top: 10, bottom: 20 }, // Padding around the label
        },
        grid: {
          display: false, // Hide X-axis grid lines
        },
        ticks: {
          color: "#666", // X-axis tick color
          font: {
            size: 12, // X-axis tick font size
          },
        },
      },
      y: {
        type: "linear", // Ensure the y-axis uses the 'linear' scale
        title: {
          display: true, // Enable the Y-axis title
          text: "Price (USD)", // Y-axis label text
          color: "#666", // Label color
          font: {
            size: 14, // Label font size
            weight: "bold", // Label font weight
          },
          padding: { top: 0, bottom: 10 }, // Padding around the label
        },
        grid: {
          color: "#eee", // Y-axis grid line color
        },
        ticks: {
          color: "#666", // Y-axis tick color
          font: {
            size: 12, // Y-axis tick font size
          },
        },
      },
    },
  };

  return (
    <div style={{ width: "100%", height: "400px" }}>
      <Line ref={chartRef} data={chartData} options={options} />
    </div>
  );
}

export default StockChart;
