import React, { useState } from "react";

function Parameters({ drift, volatility, onUpdate }) {
  const [newDrift, setNewDrift] = useState(drift);
  const [newVolatility, setNewVolatility] = useState(volatility);

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validate inputs
    if (isNaN(newDrift) || isNaN(newVolatility) || newVolatility < 0) {
      alert("Please enter valid values. Volatility must be non-negative.");
      return;
    }

    onUpdate(newDrift, newVolatility); // Call the onUpdate function
  };

  return (
    <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column", gap: "10px", margin: "20px" }}>
      <label style={{ display: "flex", flexDirection: "column", gap: "5px" }}>
        Drift:
        <input
          type="number"
          step="0.0001"
          value={newDrift}
          onChange={(e) => setNewDrift(parseFloat(e.target.value))}
          style={{ padding: "5px", width: "200px" }}
        />
      </label>
      <label style={{ display: "flex", flexDirection: "column", gap: "5px" }}>
        Volatility:
        <input
          type="number"
          step="0.01"
          value={newVolatility}
          onChange={(e) => setNewVolatility(parseFloat(e.target.value))}
          style={{ padding: "5px", width: "200px" }}
        />
      </label>
      <button
        type="submit"
        style={{
          padding: "5px 10px",
          backgroundColor: "#007bff",
          color: "#fff",
          border: "none",
          borderRadius: "3px",
          width: "fit-content",
        }}
      >
        Update Parameters
      </button>
    </form>
  );
}

export default Parameters;
