import React, { useState, useEffect } from "react";
import axios from "axios";
import SearchBar from "./SearchBar";
import StockChart from "./StockChart";
import Parameters from "./Parameters";

function App() {
  const [selectedTicker, setSelectedTicker] = useState(null);
  const [stockData, setStockData] = useState([]);
  const [drift, setDrift] = useState(0.0002);
  const [volatility, setVolatility] = useState(0.05);

  useEffect(() => {
    if (selectedTicker) {
      const fetchData = async () => {
        const response = await axios.get(`http://localhost:5000/api/stock/${selectedTicker}`);
        setStockData(response.data.prices);
      };
      fetchData();
    }
  }, [selectedTicker]);

  const handleParameterUpdate = async (newDrift, newVolatility) => {
    setDrift(newDrift);
    setVolatility(newVolatility);
    await axios.post(`http://localhost:5000/api/stock/${selectedTicker}/parameters`, {
      drift: newDrift,
      volatility: newVolatility,
    });
  };

  return (
    <div className="App">
      <h1>Real-Time Stock Prices</h1>
      <SearchBar onSelectTicker={setSelectedTicker} />
      {selectedTicker && (
        <>
          <StockChart data={stockData} />
          <Parameters
            drift={drift}
            volatility={volatility}
            onUpdate={handleParameterUpdate}
          />
        </>
      )}
    </div>
  );
}

export default App;
